$(function() {
	//var start = new Date().getTime();
	
	MyLayoutUIEditor1.options.ui_selector = ".layout-ui-editor-1";
	MyLayoutUIEditor1.init("MyLayoutUIEditor1");
	initTestHtmlCode(MyLayoutUIEditor1);
	
	MyLayoutUIEditor2.options.ui_selector = ".layout-ui-editor-2";
	MyLayoutUIEditor2.init("MyLayoutUIEditor2"); //only for testing purposes
	initTestHtmlCode2(MyLayoutUIEditor2);
	
	MyLayoutUIEditor3.options.ui_selector = ".layout-ui-editor-3";
	MyLayoutUIEditor3.init("MyLayoutUIEditor3"); //only for testing purposes
	initTestHtmlCode3(MyLayoutUIEditor3);
	
	MyLayoutUIEditor4.options.ui_selector = ".layout-ui-editor-4";
	MyLayoutUIEditor4.options.on_ready_func = function() {
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_db_brokers_func = function() {
			//console.log("calling get_db_brokers_func");
			
			return ["broker 1", "broker 2"];
		};
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_db_drivers_func = function(db_broker) {
			//console.log("calling get_db_drivers_func");
			
			if (db_broker == "broker 1")
				return ["driver 1", "driver 2"];
			else
				return ["driver 3", "driver 4"];
		};
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_db_tables_func = function(db_broker, db_driver, db_type) {
			//console.log("calling get_db_tables_func");
			
			if (db_driver == "driver 1")
				return ["table 1", "table 2"];
			else if (db_driver == "driver 2")
				return ["table 2", "table 3"];
			else if (db_driver == "driver 3")
				return ["table 4", "table 5"];
			else if (db_driver == "driver 4")
				return ["table 6", "table 7"];
		};
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_db_attributes_func = function(db_broker, db_driver, db_type, db_table) {
			//console.log("calling get_db_attributes_func");
		
			var attrs = {};
			attrs[db_table + " attr 1 pk"] = {name: "attr 1", primary_key: true};
			attrs[db_table + " attr 2"] = {name: "attr 2"};
			
			return attrs;
		};
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_resources_references_func = function() {
			//console.log("calling get_resources_references_func");
		
			return ["resource 1", "resource 2"];
		};
		MyLayoutUIEditor4.LayoutUIEditorWidgetResource.options.get_user_types_func = function() {
			//console.log("calling get_user_types_func");
		
			return ["user_type 1", "user_type 2"];
		};
	};
	MyLayoutUIEditor4.init("MyLayoutUIEditor4"); //only for testing purposes
	initTestHtmlCode3(MyLayoutUIEditor4);
	
	MyLayoutUIEditor5.options.ui_selector = ".layout-ui-editor-5";
	MyLayoutUIEditor5.options.on_ready_func = function() {
		//show view layout panel instead of code
		var luie = MyLayoutUIEditor5.getUI();
		var view_layout = luie.find(" > .tabs > .view-layout");
		var view_source = luie.find(" > .tabs > .view-source");
		view_layout.addClass("do-not-confirm");
		view_source.removeClass("is-source-changed"); //very important, otherwise the layout will be replaced with the default source and will overwrite the layout_ui_editor_iframe.html iframe contents.
		view_layout.trigger("click");
		view_layout.removeClass("do-not-confirm");
		view_source.addClass("is-source-changed");
		
		//show php widgets, borders and background
		MyLayoutUIEditor5.showTemplateWidgetsDroppableBackground();
		MyLayoutUIEditor5.showTemplateWidgetsBorders();
		MyLayoutUIEditor5.showTemplatePHPWidgets();
		
		//setTimeout(function() {
		//	MyLayoutUIEditor5.getTemplateWidgetsIframe().attr("src", "layout_ui_editor_iframe.html");
		//}, 3000);
	};
	MyLayoutUIEditor5.init("MyLayoutUIEditor5"); //only for testing purposes
	
	//only for testing purposes
	//var end = new Date().getTime();
	//console.log("TTL: " + ((end - start) / 1000));
});

function addTemplateWidgetsDroppable3() {
	var rand = parseInt(Math.random() * 1000);
	var color = Math.floor(Math.random() * 16777215).toString(16);
	
	var new_template_widgets_droppable = $('<div class="droppable main-droppable" data-main-droppable-name="Droppable ' + rand + '" style="background:#' + color + '22 !important; margin:20px !important; border:1px outset #ddd !important; width:auto !important;"></div>');
	
	var template_widgets_iframe_body = MyLayoutUIEditor3.getTemplateWidgetsIframeBody();
	template_widgets_iframe_body.append(new_template_widgets_droppable);
	
	MyLayoutUIEditor3.addTemplateWidgetsDroppable(new_template_widgets_droppable);
}

